import React, {useState} from 'react'
import {Link, Redirect} from 'react-router-dom'
import {SidebarData} from './SidebarData'
import {Preview} from './Preview'
import './Navbar.scss'

export const Navbar = (props) => {
    const [sidebar, setSidebar] = useState(false)
    const [redirect, setRedirect] = useState(false)

    const showSidebar = () => setSidebar(!sidebar) // toggle sidebar

    // logout function
    const logout = async () => {
        await fetch("http://localhost:8000/api/logout", { // send post request to logout endpoint
            method: 'POST',
            headers: {'Content-Type': 'application/json'},
            credentials: 'include',
        })

        props.setUsername('')
        setRedirect(true)
    }

    // redirect if state is true
    if(redirect) {
        return <Redirect to='/login' />
    }

    let menu;
    // if no user: don't display menu
    if (props.username === '') {
        menu = (<></>)
    } else {
        menu = (
        <>
        <div className='nav-bars'>
            <i className="fas fa-bars fa-3x" onClick={showSidebar}/>
        </div>
        <nav className={sidebar ? 'nav-menu active' : 'nav-menu'}>
            <ul className='nav-menu-items' onClick={showSidebar}>
                <li className='nav-toggle'>
                    <div className='nav-x'>
                        <i className="fas fa-times fa-3x"/>
                    </div>
                </li>
                <li className='nav-text'>
                    <Link to='/'>
                        <i className="fas fa-atom" />
                        <span> Home</span>
                    </Link>
                </li>
                <li className='nav-text'>
                    <Link to='/login' onClick={logout}>
                        <i className="fas fa-power-off" />
                        <span> Logout</span>
                    </Link>
                </li>
                <li className='mav-text'>
                    <span>{props.username}</span>
                </li>
                {SidebarData.map((item, index) => {
                    return (
                        <li key={index} className={item.className}>
                            <Preview src={item.img} alt='friend' size='img-small' name={item.name}/>
                        </li>
                    )
                })}
            </ul>
        </nav>
        </>
        )
    }

    return (
        <>
        <div className='navbar'>
            <div className='container-fluid navbar-container'/>
        {menu}
        </div>
        </>
    )
}
