import React from 'react'
import './Preview.scss'

const SIZES = ['img-large','img-small']

export const Preview = (props) => {
    const getImgSize = SIZES.includes(props.size) ? props.size : SIZES[0]; // get button style. default to primary

    // when friend is clicked function
    const clickFriend = async () => {

        await fetch("http://localhost:8000/api/check-friend", { // send post request to logout endpoint
            method: 'POST',
            headers: {'Content-Type': 'application/json'},
            credentials: 'include',
            body: JSON.stringify({
                user_id: props.user_id,
                friend_id: props.friend_id
            })
        })
        history.push('/login')
    }


    // check if friends
    // if friends:
    // Go to messages
    // else:
    // send friend request

    return (
        <div className='friend'>
            <i onClick={clickFriend}>
                <figure className='my-0'>
                    <img src={props.src} alt='friend' className={`friend mt-2 ${getImgSize}`} />
                </figure>
            </i>
            <div className='friend-name mx-auto'>
                <p>{props.name}</p>
            </div>
        </div>
    )
}
