import React from 'react'
import {Button} from './Button'
import TextField from '@material-ui/core/TextField';
import './MessageBox.scss'

export const MessageBox = (props) => {
    return (
        <div className='row mt-5' align='center'>
            <div className='col-sm-11 px-0 my-auto'>
               <input className='message-input' type="text" required />
            </div>
            <div className='col-sm-1 px-0 my-auto' align='center'>
                <Button buttonSize='btn--md'>Send</Button>
            </div>
        </div>
    )
}
