import React, {useState, useEffect} from 'react'
//import {Button} from '../components/Button'
import {Link, useHistory} from 'react-router-dom'
import {Preview} from '../components/Preview'
import {Search} from '../components/Search'
import {Popup} from '../components/Popup'
import './Home.scss'

export const Home = (props) => {
    const [search, setSearch] = useState(false)
    const [friends, setFriends] = useState('')
    const [requests, setRequests] = useState('')
    const [popup, setPopup] = useState(false)
    const [popupMessage, setPopupMessage] = useState('')
    const history = useHistory()

    const  showSearchWindow = () => setSearch(!search) // toggle search bar
    const iconClasses = search ? 'fas fa-plus-circle add-btn mb-0 rotate-icon' : 'fas fa-plus-circle add-btn mb-0'

    useEffect(() => {
        const getFriends = async() => {
            const response = await fetch("http://localhost:8000/api/get-friends", {
                headers: {'Content-Type': 'application/json'},
                credentials: 'include'
            })

            const result = await response.json()
            setFriends(result['friends']) 
            console.log("friends: ",result['friends'])
        }
        const getFriendReqs = async() => {
            const response = await fetch("http://localhost:8000/api/get-friend-reqs", {
                headers: {'Content-Type': 'application/json'},
                credentials: 'include'
            })
            const result = await response.json()
            setRequests(result['requests'])
            console.log("requests: ",result['requests'])
        }
        getFriends().catch(setFriends(''))
        getFriendReqs().catch(setRequests(''))
    }, [])

    if(!localStorage.getItem("user")) {
        history.push('/login')
    }

    return(
        <div className='container'>
            <Popup trigger={popup}>
                <h3>{popupMessage}</h3>
            </Popup>
            <div className="rooms-header">
                <h2 className="rooms-header-text mb-0">Friends</h2>
                <i className={`${iconClasses}`} onClick={showSearchWindow} />
            </div>
            <div className="search-window-container">
                <Search search={search} setSearch={setSearch}/>
            </div>
            <div className='row mx-1'>
                {friends.length === 0 ? null : Object.keys(friends).map(key => 
                    <div className='col-sm-12 col-md-4 col-lg-2 px-0'>
                        <Preview alt='friend' size='img-large' friend_id={friends[key]} setPopup={setPopup} setPopupMessage={setPopupMessage}/>
                    </div>
                )}

            </div>

            <div className="rooms-header">
                <h2 className="rooms-header-text mb-0">Pending Friends</h2>
            </div>
            <div className='row'>
                {requests.length === 0 ? null : Object.keys(requests).map(key =>
                    <div className='col-sm-12 col-md-4 col-lg-2 px-0'>
                        <Preview src='default_pic.jpeg' alt='friend' size='img-large' name='username' friend_id={requests[key]} />
                    </div>
                )}
            </div>

            <div className='rooms-header'><h2 className="rooms-header-text mt-2">Rooms</h2></div>
                <div className='row'>
                    {/*
                    <div className='col-sm-12 col-md-4 col-lg-2'>
                        <Preview src='default_pic.jpeg' name='someone'/> 
                    </div>
                    <div className='col-sm-12 col-md-4 col-lg-2'>
                        <Preview src='default_pic.jpeg' name='someone'/> 
                    </div>
                    <div className='col-sm-12 col-md-4 col-lg-2'>
                        <Preview src='default_pic.jpeg' name='someone'/> 
                    </div>
                    <div className='col-sm-12 col-md-4 col-lg-2'>
                        <Preview src='default_pic.jpeg' name='someone'/> 
                    </div>
                    <div className='col-sm-12 col-md-4 col-lg-2'>
                        <Preview src='default_pic.jpeg' name='someone'/> 
                    </div>
                    <div className='col-sm-12 col-md-4 col-lg-2'>
                        <Preview src='default_pic.jpeg' name='someone'/> 
                    </div>
                    */}
                </div>

            <p className="mt-5 mb-3 mx-auto text-muted">&copy; 2017–2021</p>

        </div>
    )
}
