import React, {useState, useEffect} from 'react'
import {useHistory} from 'react-router-dom'
import {Button} from './Button'
import '../pages/Home.scss'
import './Preview.scss'

const SIZES = ['img-large','img-small']

export const Preview = (props) => {
    const getImgSize = SIZES.includes(props.size) ? props.size : SIZES[0]; // get button style. default to primary
    const [isFriend, setIsFriend] = useState(false)
    const [profilePic, setProfilePic] = useState('')
    const [friendName, setFriendName] = useState('')
    const history = useHistory()

    // when friend is clicked function
    useEffect(() => {
        const checkFriend = async () => {
            const response = await fetch("http://localhost:8000/api/check-friend", { // send post request to logout endpoint
                method: 'POST',
                headers: {'Content-Type': 'application/json'},
                credentials: 'include',
                body: JSON.stringify({
                    friend_id: props.friend_id
                })
            })

            const result = await response.json()

            if (result['message'] === 'true') {
                setIsFriend(true)
            } else {
                setIsFriend(false)
            }
        }
        const getFriendInfo = async () => {
            const response = await fetch("http://localhost:8000/api/get-friend-info", { // send post request to logout endpoint
                method: 'POST',
                headers: {'Content-Type': 'application/json'},
                credentials: 'include',
                body: JSON.stringify({
                    friend_id: props.friend_id
                })
            })

            const result = await response.json()

            if (result['message'] === 'Could Not Find User') {
                console.log(result)
            } else {
                setFriendName(result['username'])
                setProfilePic(result['profile_pic'])
            }
        }
        getFriendInfo()
        checkFriend()
    }, [props.friend_id])

    const addFriend = async() => {
        const response = await fetch("http://localhost:8000/api/add-friend", { // send post request to logout endpoint
            method: 'POST',
            headers: {'Content-Type': 'application/json'},
            credentials: 'include',
            body: JSON.stringify({
                friend_id: props.friend_id
            })
        })

        const result = await response.json()

        console.log(result)
        history.push('/')
    }

    return (
        <div>
            <div className='friend-container'>
                <img src={profilePic} alt='friend' className={`mt-2 ${getImgSize}`} />
                { getImgSize === 'img-large' ? 
                        <div class={`overlay ${getImgSize}`}>
                            {isFriend === false ?
                                <Button buttonStyle='btn--primary' buttonSize='btn--sm' classes='overlay-btn' type="submit" onClick={addFriend}>Add Friend</Button>
                            :
                                <Button buttonStyle='btn--primary' buttonSize='btn--sm' type="submit" classes='mx-auto overlay-btn'>Message</Button>
                            }
                            {isFriend === true ?
                                <Button buttonStyle='btn--primary' buttonSize='btn--sm' type="submit" classes='overlay-btn'>Remove Friend</Button>
                                : null}
                        </div>
                        : null }
            </div>
            <div className='friend-name'>
                <p>{friendName}</p>
            </div>
        </div>
    )
}
