import './App.css';
import React, {useState} from 'react'
import { Navbar } from './components/Navbar';
import {BrowserRouter, Route} from 'react-router-dom';
import {Home} from './pages/Home';
import {Login} from './pages/Login';
import {Register} from './pages/Register';
import {Friend} from './pages/Friend'
import {Room} from './pages/Room'

function App() {
    const [username, setUsername] = useState('')

    // Get user if logged in
    useEffect(() => {(
            async() => {
                const response = await fetch("http://localhost:8000/api/getuser", {
                    headers: {'Content-Type': 'application/json'},
                    credentials: 'include'
                })

                const user = await response.json()

                if (user.message === undefined) {
                    setUsername(user.username)
                }

            }
        )()}
    )

  return (
    <>
        <BrowserRouter>
            <Navbar username={username} setUsername={setUsername} />
            <Route path='/' exact   component={() => <Home/>}/>
            <Route path='/login'    component={() => <Login username={username} setUsername={setUsername}/>}/>
            <Route path='/register' component={() => <Register username={username}/>}/>
            <Route path='/friend'   component={() => <Friend username={username}/>}/>
            <Route path='/room'     component={() => <Room username={username}/>}/>
        </BrowserRouter>

    </>
  );
}

export default App;
