import React, {useEffect, useState} from 'react'
import {useHistory, useParams} from 'react-router-dom'
import {Preview} from '../components/Preview'
import {Message} from '../components/Message'
import {MessageBox} from '../components/MessageBox'
import './RoomFriend.scss'

export const Room = (props) => {
    const history = useHistory()
    let router_data = useParams()
    const [messages, setMessages] = useState('')
    let room_id = router_data['room_id']

    useEffect(() => {
        const getMessages = async() => {
            const response = await fetch("http://localhost:8000/api/get-friends", {
                headers: {'Content-Type': 'application/json'},
                credentials: 'include'
            })

            const result = await response.json()
            setMessages(result['messages']) 
            console.log("messages: ",result['messages'])
        }
    })

    if (!localStorage.getItem("user")) {
        history.push('/login')
    }

    return (
        <div className='container room-friend-container'>
            <div className='row mt-5'>
                <Preview alt='room' size='img-large' isRoom={true} room_id={room_id} />
            </div>
            <div className='row'>
                {messages.length === 0 ? null : Object.keys(messages).map(key => 
                    <div key={key} className='col-sm-12 col-md-4 col-lg-2 px-0'>
                        <Message user={message[key]['name']} text={message[key]['text']}  />
                    </div>
                )}
                <Message user='Me' text='Hello World' />
            </div>

            <div className='row'>
                <MessageBox />
            </div>
        </div>
    )
}
