import React, {useState} from 'react'
import {useHistory} from 'react-router-dom'
import '../pages/Home.scss'
import './Preview.scss'

const SIZES = ['img-large','img-small']

export const Preview = (props) => {
    const getImgSize = SIZES.includes(props.size) ? props.size : SIZES[0]; // get button style. default to primary
    const [isFriend, setIsFriend] = useState(false)
    const [showSelect, setShowSelect] = useState(false)
    const history = useHistory()

    // when friend is clicked function
    const clickFriend = async () => {
        const response = await fetch("http://localhost:8000/api/check-friend", { // send post request to logout endpoint
            method: 'POST',
            headers: {'Content-Type': 'application/json'},
            credentials: 'include',
            body: JSON.stringify({
                friend_id: props.friend_id
            })
        })

        const result = await response.json()

        setShowSelect(!showSelect) // toggle show select menu

        if (result['message'] === 'Success') {
            setIsFriend(true)
        } else {
            setIsFriend(false)
            const response = await fetch("http://localhost:8000/api/add-friend", { // send post request to logout endpoint
                method: 'POST',
                headers: {'Content-Type': 'application/json'},
                credentials: 'include',
                body: JSON.stringify({
                    user_id: props.user_id,
                    friend_id: props.friend_id
                })
            })

            const data = await response.json()
            if (data.message === 'Success') {
                console.log("friend added")
            } else {
                console.log("couldn't add friend")
            }
        }
    }

    const iconClasses = showSelect ? 'fas fa-plus-circle add-btn mb-0 rotate-icon' : 'fas fa-plus-circle add-btn mb-0'


    // check if friends
    // if friends:
    // Go to messages
    // else:
    // send friend request

    return (
        <div className='friend'>
                <figure className='my-0 preview-link'>
                    <img src={props.src} alt='friend' className={`friend mt-2 ${getImgSize}`} />
                        <i className={iconClasses} onClick={clickFriend} />
                    { showSelect ? isFriend ?  // if friend: give options to message or remove
                        <select>               
                            <option value='Message'>Message</option>
                            <option value='Remove'>Remove Friend</option>
                        </select>
                        :                          // if not friend: give option to add
                        <select>
                            <option value='Add Friend'>Add Friend</option>
                        </select> // if showSelect is false: don't show menu}
                        : null}                 
                </figure>
            <div className='friend-name mx-auto'>
                <p>{props.name}</p>
            </div>
        </div>
    )
}
