import React from 'react';
import './Button.scss';
import {Link} from 'react-router-dom';

const STYLES = ['btn--primary', 'btn--secondary', 'btn--outline'];
const SIZES  = ['btn--med', 'btn--lg']

export const Button = ({children, type, onClick, buttonStyle, buttonSize, link}) => {
    const getButtonStyle = STYLES.includes(buttonStyle) ? buttonStyle : STYLES[0]; // get button style. default to primary
    const getButtonSize = SIZES.includes(buttonSize) ? buttonSize : SIZES[0];      // get button size. default to medium
    const getLink = link ? link : ''

    return (
        <Link to={getLink} className=''>
            <button className={`btn ${getButtonSize} ${getButtonStyle}`} onClick={onClick} type={type}>
                {children}
            </button>
        </Link>
    )
}
