import React, {SyntheticEvent, useState} from 'react'
import {Button} from './Button'
//import {Link} from 'react-router-dom'
import TextField from '@material-ui/core/TextField';
import './Search.scss'
import './Message.scss'

export const Search = (props) => {
    const [error, setError] = useState('')

    const submit = async(e: SyntheticEvent) => {
        e.preventDefault()

        const response = await fetch("http://localhost:8000/api/login", { // send post request to login endpoint
            method: 'POST',
            headers: {'Content-Type': 'application/json'},
            credentials: 'include',
            body: JSON.stringify({
                username: pass
            })
        })

        const user = await response.json() // get content
        console.log("Login", user)

        if (user.message !== undefined) {
            setError(user.message)
        } else {
            localStorage.setItem("user", user)
            props.setUser(user) // set user to logged in user
            setError('')
            setRedirect(true) // set redirect to true
        }

    }

    return (
        <div className={props.search ? 'search-window active' : 'search-window'} >
            <div className='search-box'>
            <div className='mt-5' align='center'>
                <TextField
                    label="Search..."
                    multiline
                    maxRows={4}
                    variant="filled"
                />
                <Button buttonSize='btn--md' buttonStyle='btn--secondary' type='submit' my='mt-2' mx='mx-auto' onClick={submit}>Search</Button>
            </div>
            </div>
        </div>
    )
}
