import React, {SyntheticEvent, useState} from 'react'
import {Button} from './Button'
//import {Link} from 'react-router-dom'
import TextField from '@material-ui/core/TextField';
import './Search.scss'
import './Message.scss'

export const Search = (props) => {
    const [error, setError] = useState('')
    const [username, setUsername] = useState('')
    const [receivedData, setReceivedData] = useState(false)
    const [data, setData] = useState('')

    const submit = async(e: SyntheticEvent) => {
        e.preventDefault()

        const response = await fetch("http://localhost:8000/api/search-friend", { // send post request to login endpoint
            method: 'POST',
            headers: {'Content-Type': 'application/json'},
            credentials: 'include',
            body: JSON.stringify({
                username: username
            })
        })

        const result = await response.json() // get content

        if (result !== null) {
            setError('')
            setReceivedData(true)
            setData(result)
            console.log(data)
        } else {
            setError(result)
            setReceivedData(false)
            setData('')
        }

    }

    return (
        <div className={props.search ? 'search-window active' : 'search-window'} >
            <div className='search-box'>
            <div className='mt-5' align='center'>
                <TextField
                    label="Search..."
                    multiline
                    maxRows={4}
                    variant="filled"
                    onChange={e=>setUsername(e.target.value)}
                />
                <Button buttonSize='btn--md' buttonStyle='btn--secondary' type='submit' my='mt-2' mx='mx-auto' onClick={submit}>Search</Button>
                {error}
                {receivedData ? Object.keys(data).map(key => 
                    <li key={key}>{data[key]['username']}</li>) 
                : null}
            </div>
            </div>
        </div>
    )
}
