import React, {useEffect, useState} from 'react'
import {useHistory, useParams} from 'react-router-dom'
import {Preview} from '../components/Preview'
import {Message} from '../components/Message'
import {MessageBox} from '../components/MessageBox'
import './RoomFriend.scss'


export const Friend = (props) => {
    const history = useHistory()
    const [msg, setMsg] = useState()

    let router_data = useParams()
    let friend_id = router_data['friend_id']

    if (!localStorage.getItem("user")) {
        history.push('/login')
    }

    /*
    const getMessages = async() => {
        const response = await fetch("http://localhost:8000/api/get-messages", { // send post request to logout endpoint
            method: 'POST',
            headers: {'Content-Type': 'application/json'},
            credentials: 'include',
            body: JSON.stringify({
                friend_id: friend_id
            })
        })

        const result = await response.json()
        return result
    }
    */

    const [messages, setMessages] = useState([])

    let socket = new WebSocket("ws://localhost:8000/ws/"+friend_id)

    useEffect(() => {
        socket.onopen = () => {
            console.log("YEEEE from the client")
        }
        socket.onmessage = (msg) => {
            console.log(msg.data)
            setMessages([...messages, msg])
            console.log(messages)
        }

        socket.onclose = (event) => {
            console.log("socket closed connection: ", event)
        }

        const getMessages = async() => {
            const response = await fetch("http://localhost:8000/api/get-messages", { // send post request to logout endpoint
                method: 'POST',
                headers: {'Content-Type': 'application/json'},
                credentials: 'include',
                body: JSON.stringify({
                    friend_id: friend_id
                })
            })

            const result = await response.json()
            console.log(result)
        }
        getMessages()

    })

    const sendMsg = () => {
        socket.send(msg)
    }

    return (
        <div className='container room-friend-container'>
            <div className='row mt-5'>
                <Preview alt='friend' size='img-large' isRoom={false} friend_id={friend_id} />
            </div>

            <div className='row'>
                {messages.length === 0 ? null : Object.keys(messages).map(key => 
                    <div key={key} className='col-sm-12 col-md-4 col-lg-2 px-0'>
                        <Message user={messages[key]['user']} text={messages[key]['text']}/>
                    </div>
                )}
            </div>

            <div className='row'>
                <MessageBox onChange={setMsg} onClick={sendMsg}/>
            </div>
        </div>
    )
}
