import React, {useState, useEffect} from 'react'
import {Redirect} from 'react-router-dom'
import {Preview} from '../components/Preview'
import {Message} from '../components/Message'
import {MessageBox} from '../components/MessageBox'
import './RoomFriend.scss'

export const Friend = (props) => {
    const [redirect, setRedirect] = useState(false)

    useEffect(() => {
        if (props.username === '') {        // if user is not authenticated:
            return <Redirect to='/login' /> // redirect to login
        }
    },[props.username])

    return (
        <div className='container room-friend-container'>
            <div className='row mt-5'>
                <Preview src='default_pic.jpeg' name='someone' /> 
            </div>

            <div className='row'>
                <Message user='Me' text='Hello World' />
            </div>

            <div className='row'>
                <MessageBox />
            </div>

        </div>
    )
}
