import React, {useEffect} from 'react'
import io from 'socket.io-client'
import {useHistory, useParams} from 'react-router-dom'
import {Preview} from '../components/Preview'
import {Message} from '../components/Message'
import {MessageBox} from '../components/MessageBox'
import './RoomFriend.scss'

const socket = io("ws://localhost:8000/chat/123", {
    transports: ['websocket', 'polling']
})

export const Friend = (props) => {
    const history = useHistory()
    let router_data = useParams()
    let friend_id = router_data['friend_id']
    console.log("friend id: ", friend_id)

    if (!localStorage.getItem("user")) {
        history.push('/login')
    }


    useEffect(() => {
        /*
        socket.on('connect', () => {
            socket.emit('i am here')
        })
        */
        const getMessages = async() => {
            const response = await fetch("http://localhost:8000/api/get-messages", { // send post request to logout endpoint
                method: 'POST',
                headers: {'Content-Type': 'application/json'},
                credentials: 'include',
                body: JSON.stringify({
                    friend_id: props.friend_id
                })
            })

            const result = await response.json()
        }

        getMessages()

    })

    return (
        <div className='container room-friend-container'>
            <div className='row mt-5'>
                <Preview alt='friend' size='img-large' isRoom={false} friend_id={friend_id} />
            </div>

            <div className='row'>
            </div>

            <div className='row'>
                <MessageBox />
            </div>
        </div>
    )
}
