import React, {useEffect, useState, useRef} from 'react'
import {useHistory, useParams} from 'react-router-dom'
import {Preview} from '../components/Preview'
import {Message} from '../components/Message'
import {MessageBox} from '../components/MessageBox'
import './RoomFriend.scss'

//const socket = new WebSocket("ws://localhost:8000/ws/234");

export const Friend = (props) => {
    const history = useHistory()
    const [msg, setMsg] = useState()
    const [messages, setMessages] = useState([])
    const room_id = useRef(null);
    const socket = useRef(null);
    //let socket = new WebSocket("ws://localhost:8000/ws/123")

    let router_data = useParams()
    let friend_id = router_data['friend_id']

    if (!localStorage.getItem("user")) {
        history.push('/login')
    }


    useEffect(() => {
        const getFriendChat = async() => {
            const response = await fetch("http://localhost:8000/api/get-friend-chat", { // send post request to logout endpoint
                method: 'POST',
                headers: {'Content-Type': 'application/json'},
                credentials: 'include',
                body: JSON.stringify({
                    friend_id: friend_id
                })
            })

            const result = await response.json()
            room_id.current = result['room_id']
            console.log("room id: ", room_id.current)
            return
        }

        const getMessages = async() => {
            const response = await fetch("http://localhost:8000/api/get-messages", { // send post request to logout endpoint
                method: 'POST',
                headers: {'Content-Type': 'application/json'},
                credentials: 'include',
                body: JSON.stringify({
                    room_id: room_id
                })
            })

            const result = await response.json()
            setMessages(result['messages'])
        }
        getFriendChat()
        console.log("room id after getFriendChat: ", room_id.current)
        getMessages()

        socket.current = new WebSocket("ws://localhost:8000/ws/"+room_id.current)

        socket.current.onopen = (event) => {
            console.log("Connection at: ", "ws://localhost:8000/ws/"+room_id.current)
        }
        socket.current.onmessage = (msg) => {
            let new_msg = JSON.parse(msg.data)
            console.log(new_msg)
            setMessages(prev => [...prev, new_msg])
        }

        socket.current.onclose = (event) => {
            console.log("socket closed connection: ", event)
        }

        return () => socket.current.close()

    },[])

    const sendMsg = () => {
        socket.current.send(msg)
    }

    return (
        <div className='container room-friend-container'>
            <div className='row mt-5'>
                <Preview alt='friend' size='img-large' isRoom={false} friend_id={friend_id} />
            </div>

            <div className='row'>
                {messages.length === 0 ? null : Object.keys(messages).map(key => 
                    <div key={key} className=''>
                        <Message user={messages[key]['user']} text={messages[key]['text']}/>
                    </div>
                )}
            </div>

            <div className='row'>
                <MessageBox onChange={setMsg} onClick={sendMsg}/>
            </div>
        </div>
    )
}
